export default class MovingImage{
    constructor(image, startTop=50, startLeft=50, newTop=50, newLeft=50){
        this.image=image;
        this.startTop=startTop;
        this.startLeft=startLeft;
        this.newTop=newTop;
        this.newLeft=newLeft;
        this.piece=null;
        this.velocity=.02;
        this.createPiece();
        this.direction = { x: Math.cos(this.newLeft), y: Math.sin(this.newTop) }
        setTimeout(()=>{
            this.destroyPiece()
        }, 3000)
    }
    get x(){
        return parseFloat(getComputedStyle(this.piece).getPropertyValue("--x"))
    }
