        this.counter = 1
        console.log(startTop, startLeft)
        setTimeout(()=>{
            this.destroyPiece()
        }, 3000)
    }
    get x(){
        return parseFloat(getComputedStyle(this.piece).getPropertyValue("--x"))
    }
